document.addEventListener("DOMContentLoaded",(()=>{document.getElementById("all").addEventListener("click",(async()=>{let e=await fetch("./users.json"),t=(await e.json()).users.sort((function(e,t){let n=e.lastName.toUpperCase(),a=t.lastName.toUpperCase();return n<a?-1:n>a?1:0})),n=document.getElementById("adatok");n.textContent="";for(let e of t){let t=document.createElement("li");t.innerHTML=e.lastName.toUpperCase()+" "+e.firstName,n.appendChild(t)}}))}));