document.addEventListener("DOMContentLoaded",(()=>{document.getElementById("all").addEventListener("click",(async()=>{let e=await fetch("./users.json"),t=(await e.json()).users.sort((function(e,t){let n=e.lastName.toUpperCase(),a=t.lastName.toUpperCase();return n<a?-1:n>a?1:0})),n=document.getElementById("adatok");n.textContent="",document.getElementById("table").innerHTML="";for(let e of t){let t=document.createElement("li");t.innerHTML=e.lastName.toUpperCase()+"&emsp;"+e.firstName,n.appendChild(t)}})),document.getElementById("contact").addEventListener("click",(async()=>{let e=await fetch("./users.json"),t=(await e.json()).users.sort((function(e,t){let n=e.username.toUpperCase(),a=t.username.toUpperCase();return n<a?-1:n>a?1:0}));document.getElementById("adatok").textContent="";let n=document.getElementById("table");n.innerHTML="";for(let e of t){let t=document.createElement("tr"),a=document.createElement("td"),d=document.createElement("td"),o=document.createElement("td");a.textContent=e.username,d.textContent=e.email,o.textContent=e.phone,t.appendChild(a),t.appendChild(d),t.appendChild(o),n.appendChild(t)}}))}));